import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Table, Button, Alert, Modal, Card } from 'react-bootstrap';
import { FaEdit, FaTrash } from 'react-icons/fa'; // Importa los iconos para editar y eliminar

// Estilos CSS personalizados con animaciones para Gestión de Alumnos
const customStyles = `
/* Card principal sin movimiento */
.gestion-card {
    transition: box-shadow 0.3s ease;
    border: none !important;
}

.gestion-card:hover {
    box-shadow: 0 8px 25px rgba(0,0,0,0.15) !important;
}

/* Título sin animaciones */
.gestion-title {
    color: #333;
    font-weight: bold;
}

.gestion-title::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 50%;
    width: 0;
    height: 3px;
    background: linear-gradient(90deg, #007bff, #28a745);
    transition: all 0.3s ease;
    transform: translateX(-50%);
}

.gestion-title:hover::after {
    width: 100%;
}

/* Botón sin movimiento del contenedor */
.btn-crear-alumno {
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.btn-crear-alumno::before {
    content: '';
    position: absolute;
    top: 0;
