import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import GestionAlumnos from './components/GestionAlumnos';
import CrearAlumno from './components/CrearAlumno';
import EditarAlumno from './components/EditarAlumno';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Nav, Navbar } from 'react-bootstrap';

// Estilos CSS personalizados con animaciones para la barra de navegación
const navbarStyles = `
/* Animación de la barra de navegación completa */
.navbar-animated {
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    backdrop-filter: blur(10px);
    background: linear-gradient(135deg, rgba(248,249,250,0.95) 0%, rgba(255,255,255,0.9) 100%) !important;
    border-bottom: 2px solid transparent;
    box-shadow: 0 2px 20px rgba(0,0,0,0.1);
}

.navbar-animated:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 30px rgba(0,123,255,0.15);
    border-bottom: 2px solid #007bff;
    background: linear-gradient(135deg, rgba(255,255,255,0.98) 0%, rgba(248,249,250,0.95) 100%) !important;
}

/* Animación del brand/logo */
.navbar-brand-animated {
    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    position: relative;
    overflow: hidden;
    font-weight: bold !important;
    font-size: 1.4rem !important;
}

.navbar-brand-animated::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0,123,255,0.2), transparent);
    transition: left 0.6s ease;
}

.navbar-brand-animated:hover::before {
    left: 100%;
}

.navbar-brand-animated:hover {
    transform: scale(1.1) translateY(-1px);
    color: #007bff !important;
    text-shadow: 0 2px 10px rgba(0,123,255,0.3);
}

.navbar-brand-animated:active {
    transform: scale(1.05) translateY(0px);
}

/* Animación de los enlaces de navegación */
.nav-link-animated {
    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    position: relative;
    margin: 0 10px;
    padding: 8px 16px !important;
    border-radius: 20px;
    font-weight: 500;
}

.nav-link-animated::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 3px;
    background: linear-gradient(90deg, #007bff, #dc3545);
    transition: all 0.3s ease;
    transform: translateX(-50%);
    border-radius: 2px;
}

.nav-link-animated:hover {
    transform: translateY(-3px) scale(1.05);
    background-color: rgba(0,123,255,0.1) !important;
    color: #007bff !important;
    box-shadow: 0 4px 15px rgba(0,123,255,0.2);
}

.nav-link-animated:hover::after {
    width: 80%;
}

.nav-link-animated:active {
    transform: translateY(-1px) scale(1.02);
}

/* Estilo especial para enlaces activos */
.nav-link-active {
    background: linear-gradient(135deg, #007bff, #0056b3) !important;
    color: white !important;
    box-shadow: 0 4px 15px rgba(0,123,255,0.4);
    transform: translateY(-2px);
}

.nav-link-active:hover {
    background: linear-gradient(135deg, #0056b3, #004085) !important;
    color: white !important;
    transform: translateY(-4px) scale(1.05);
    box-shadow: 0 6px 20px rgba(0,123,255,0.5);
}

.nav-link-active::after {
    width: 100%;
    background: rgba(255,255,255,0.5);
}

/* Animación del botón toggle para móvil */
.navbar-toggler-animated {
    transition: all 0.3s ease;
    border: none !important;
    padding: 6px 10px;
}

.navbar-toggler-animated:hover {
    transform: rotate(90deg) scale(1.1);
    background-color: rgba(0,123,255,0.1);
}

.navbar-toggler-animated:focus {
    box-shadow: 0 0 0 0.2rem rgba(0,123,255,0.25);
}

/* Animación del contenedor */
.navbar-container-animated {
    transition: all 0.3s ease;
}

.navbar-animated:hover .navbar-container-animated {
    transform: scale(1.01);
}

/* Efectos de entrada para los elementos de navegación */
@keyframes slideInFromTop {
    from {
        opacity: 0;
        transform: translateY(-30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.navbar-animated {
    animation: slideInFromTop 0.6s ease-out;
}

.nav-link-animated:nth-child(1) {
    animation: slideInFromTop 0.8s ease-out;
}

.nav-link-animated:nth-child(2) {
    animation: slideInFromTop 1s ease-out;
}

/* Efecto de pulsación en toda la navbar */
.navbar-animated:active {
    transform: translateY(0px) scale(0.99);
}
`;

// Agregar los estilos al documento
if (!document.getElementById('navbar-animation-styles')) {
    const styleSheet = document.createElement("style");
    styleSheet.type = "text/css";
    styleSheet.id = "navbar-animation-styles";
    styleSheet.innerText = navbarStyles;
    document.head.appendChild(styleSheet);
}


// Componente de navegación con resaltado activo
function Navigation() {
    const location = useLocation();

    // Función para determinar si un enlace está activo
    const isActive = (path) => {
        if (path === '/' && location.pathname === '/') return true;
        if (path === '/alumnos' && (location.pathname === '/alumnos' || location.pathname.startsWith('/editar'))) return true;
        if (path === '/crear' && location.pathname === '/crear') return true;
        return false;
    };

    return (
        <Navbar bg="light" expand="lg">
            <Container>
                <Navbar.Brand 
                    as={Link} 
                    to="/" 
                    style={{ 
                        color: isActive('/') && location.pathname === '/' ? '#212529' : '#212529',
                        fontWeight: isActive('/') && location.pathname === '/' ? 'bold' : 'normal'
                    }}
                >
                    Gestión de Alumnos
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link 
                            as={Link} 
                            to="/alumnos"
                            style={{ 
                                color: isActive('/alumnos') ? '#0d6efd' : '#212529',
                                fontWeight: isActive('/alumnos') ? 'bold' : 'normal'
                            }}
                        >
                            Gestión de Alumnos
                        </Nav.Link>
                        <Nav.Link 
                            as={Link} 
                            to="/crear"
                            style={{ 
                                color: isActive('/crear') ? '#0d6efd' : '#212529',
                                fontWeight: isActive('/crear') ? 'bold' : 'normal'
                            }}
                        >
                            Crear Alumno
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}

function App() {
    return (
        <Router>
            <Navigation />
            <Container className="mt-3" style={{ minHeight: 'calc(100vh - 120px)' }}>
                <Routes>
                    <Route path="/" element={<Dashboard />} />
                    <Route path="/alumnos" element={<GestionAlumnos />} />
                    <Route path="/crear" element={<CrearAlumno />} />
                    <Route path="/editar/:id" element={<EditarAlumno />} /> {/* Nueva ruta para editar */}
                </Routes>
            </Container>

            {/* Footer */}
                  {/* Footer */}
      <footer className="footer mt-auto py-2 bg-dark text-white border-top border-info">
        <div className="container">
          <div className="text-center">
            <p className="mb-0 text-muted small">
              &copy; 2025 Sistema de Gestión de Alumnos
            </p>
          </div>
        </div>
      </footer>
        </Router>
    );
}


export default App;
