import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { Form, Button, Alert, Card } from 'react-bootstrap';

// Estilos CSS personalizados con animaciones para CrearAlumno
const animationStyles = `
/* Card principal sin movimiento */
.crear-alumno-card {
    transition: box-shadow 0.3s ease;
    border: none !important;
}

.crear-alumno-card:hover {
    box-shadow: 0 8px 25px rgba(0,0,0,0.15) !important;
}

/* Título sin movimiento del contenedor */
.crear-alumno-title {
    transition: color 0.3s ease;
    position: relative;
    overflow: hidden;
}

.crear-alumno-title:hover {
    color: #007bff;
}

.crear-alumno-title::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 50%;
    width: 0;
    height: 3px;
    background: linear-gradient(90deg, #007bff, #dc3545);
    transition: all 0.3s ease;
    transform: translateX(-50%);
}

.crear-alumno-title:hover::after {
    width: 100%;
}

/* Campos del formulario sin movimiento */
.form-control-animated {
    transition: all 0.3s ease;
    border: 2px solid #ced4da;
}

.form-control-animated:focus {
    box-shadow: 0 2px 8px rgba(0,123,255,0.2);
    border-color: #007bff;
}

.form-control-animated:hover {
    border-color: #007bff;
    box-shadow: 0 1px 4px rgba(0,123,255,0.1);
}

/* Etiquetas sin movimiento */
.form-label-animated {
    transition: color 0.3s ease;
    font-weight: 500;
}

.form-label-animated:hover {
    color: #007bff;
}

/* Botón de crear sin movimiento */
.btn-crear-animated {
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.btn-crear-animated::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s;
}

.btn-crear-animated:hover::before {
    left: 100%;
}

.btn-crear-animated:hover {
    box-shadow: 0 4px 15px rgba(0,123,255,0.3);
    background-color: #0056b3 !important;
    border-color: #0056b3 !important;
}

/* Botón de cancelar sin movimiento */
.btn-cancelar-animated {
    transition: all 0.3s ease;
}

.btn-cancelar-animated:hover {
    box-shadow: 0 3px 10px rgba(220,53,69,0.3);
    background-color: #c82333 !important;
    border-color: #bd2130 !important;
}
`;

// Agregar los estilos al documento
if (!document.getElementById('crear-alumno-animation-styles')) {
    const styleSheet = document.createElement("style");
    styleSheet.type = "text/css";
    styleSheet.id = "crear-alumno-animation-styles";
    styleSheet.innerText = animationStyles;
    document.head.appendChild(styleSheet);
}


// Componente CrearAlumno: permite crear un nuevo alumno enviando los datos a una API REST
const CrearAlumno = () => {
    // Estado inicial del formulario
    const [alumno, setAlumno] = useState({
        nombre: '', // Nombre del alumno
        numero_documento: '', // Número de documento del alumno
        nota1: '', // Nota 1 del alumno
        nota2: '', // Nota 2 del alumno
        nota3: '', // Nota 3 del alumno
    });


    // Hook para redirigir al usuario a otra ruta
    const navigate = useNavigate();


    // Estado para mostrar alertas (éxito o error)
    const [showAlert, setShowAlert] = useState(null);


    // Maneja los cambios en los campos del formulario
    const handleChange = (e) => {
        // Actualiza el estado del formulario con el valor ingresado
        setAlumno({ ...alumno, [e.target.name]: e.target.value });
    };


    // Maneja el envío del formulario
    const handleSubmit = async (e) => {
        e.preventDefault(); // Previene el comportamiento por defecto del formulario
        try {
            // Envía los datos del formulario a la API
            await axios.post('http://127.0.0.1:8000/api/alumnos/', alumno);
            // Muestra una alerta de éxito
            setShowAlert({ variant: 'success', message: 'Alumno creado exitosamente!' });
            // Redirige al usuario a la lista de alumnos después de 1.5 segundos
            setTimeout(() => navigate('/alumnos'), 1500);
        } catch (error) {
            // Muestra una alerta de error si ocurre un problema
            console.error("Error al crear alumno:", error);
            console.error("Error response:", error.response?.data);
            console.error("Error status:", error.response?.status);
            
            let errorMessage = 'Error al crear el alumno.';
            
            if (error.response?.data) {
                // Manejo específico de errores de validación
                if (typeof error.response.data === 'object') {
                    const errors = error.response.data;
                    
                    // Error específico para número de documento duplicado
                    if (errors.numero_documento) {
                        errorMessage = `❌ El número de documento "${alumno.numero_documento}" ya está registrado. Por favor, use un número diferente.`;
                    }
                    // Error específico para nombre
                    else if (errors.nombre) {
                        errorMessage = `❌ Error en el nombre: ${errors.nombre.join(', ')}`;
                    }
                    // Errores en las notas
                    else if (errors.nota1 || errors.nota2 || errors.nota3) {
                        const notaErrors = [];
                        if (errors.nota1) notaErrors.push(`Nota 1: ${errors.nota1.join(', ')}`);
                        if (errors.nota2) notaErrors.push(`Nota 2: ${errors.nota2.join(', ')}`);
                        if (errors.nota3) notaErrors.push(`Nota 3: ${errors.nota3.join(', ')}`);
                        errorMessage = `❌ Error en las notas: ${notaErrors.join(', ')}`;
                    }
                    // Otros errores
                    else {
                        errorMessage = `❌ ${Object.values(errors).flat().join(', ')}`;
                    }
                } else {
                    errorMessage = `❌ ${error.response.data}`;
                }
            } else if (error.message) {
                errorMessage = `❌ ${error.message}`;
            }
            
            setShowAlert({ variant: 'danger', message: errorMessage });
        }
    };


    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-md-6 col-lg-5">
                    {/* Título del formulario con animación */}
                    <h2 className="text-center mb-4 crear-alumno-title">Crear Nuevo Alumno</h2>

                    {/* Card contenedor del formulario con animación */}
                    <Card className="shadow-lg border-0 crear-alumno-card">
                        <Card.Body className="p-4">
                            {/* Alerta condicional: se muestra si hay un mensaje en showAlert */}
                            {showAlert && (
                                <Alert variant={showAlert.variant} onClose={() => setShowAlert(null)} dismissible>
                                    {showAlert.message}
                                </Alert>
                            )}

                            {/* Formulario para crear un nuevo alumno */}
                            <Form onSubmit={handleSubmit}>
                {/* Campo para el nombre del alumno */}
                <Form.Group className="mb-3">
                    <Form.Label className="form-label-animated">Nombre</Form.Label>
                    <Form.Control
                        type="text"
                        name="nombre"
                        value={alumno.nombre}
                        onChange={handleChange}
                        required
                        className="form-control-animated"
                    />
                </Form.Group>


                {/* Campo para el número de documento */}
                <Form.Group className="mb-3">
                    <Form.Label className="form-label-animated">Número de Documento</Form.Label>
                    <Form.Control
                        type="text"
                        name="numero_documento"
                        value={alumno.numero_documento}
                        onChange={handleChange}
                        required
                        className="form-control-animated"
                    />
                </Form.Group>


                {/* Campo para la Nota 1 */}
                <Form.Group className="mb-3">
                    <Form.Label className="form-label-animated">Nota 1</Form.Label>
                    <Form.Control
                        type="number"
                        step="0.01"
                        name="nota1"
                        value={alumno.nota1}
                        onChange={handleChange}
                        required
                        className="form-control-animated"
                    />
                </Form.Group>


                {/* Campo para la Nota 2 */}
                <Form.Group className="mb-3">
                    <Form.Label className="form-label-animated">Nota 2</Form.Label>
                    <Form.Control
                        type="number"
                        step="0.01"
                        name="nota2"
                        value={alumno.nota2}
                        onChange={handleChange}
                        required
                        className="form-control-animated"
                    />
                </Form.Group>


                {/* Campo para la Nota 3 */}
                <Form.Group className="mb-3">
                    <Form.Label className="form-label-animated">Nota 3</Form.Label>
                    <Form.Control
                        type="number"
                        step="0.01"
                        name="nota3"
                        value={alumno.nota3}
                        onChange={handleChange}
                        required
                        className="form-control-animated"
                    />
                </Form.Group>


                {/* Botón para enviar el formulario con animación */}
                <Button variant="primary" type="submit" className="btn-crear-animated">Guardar</Button>


                {/* Enlace para cancelar y volver a la página principal con animación */}
                <Link to="/" className="btn btn-danger ms-2 btn-cancelar-animated">Cancelar</Link>
            </Form>
                        </Card.Body>
                    </Card>
                </div>
            </div>
        </div>
    );
};


export default CrearAlumno;
